OCTOSM <- function(A0,A1,A2,A3,A4,A5,A6,A7,G0,G1,G2,G3,E4,E5,E6,E7)
  {
  D1=(A0*G0-A1*G1-A2*G2-A3*G3)-(E4*A4-(-1*E5)*A5-(-1*E6)*A6-(-1*E7)*A7)
  D2=(A0*G1+A1*G0+A2*G3-A3*G2)-(E4*A5+(-1*E5)*A4+(-1*E6)*A7-(-1*E7)*A6)
  D3=(A0*G2-A1*G3+A2*G0+A3*G1)-(E4*A6-(-1*E5)*A7+(-1*E6)*A4+(-1*E7)*A5)
  D4=(A0*G3+A1*G2-A2*G1+A3*G0)-(E4*A7+(-1*E5)*A6-(-1*E6)*A5+(-1*E7)*A4)
  D5=(E4*A0-E5*A1-E6*A2-E7*A3)+(A4*G0-A5*(-1*G1)-A6*(-1*G2)-A7*(-1*G3))
  D6=(E4*A1+E5*A0+E6*A3-E7*A2)+(A4*(-1*G1)+A5*G0+A6*(-1*G3)-A7*(-1*G2))
  D7=(E4*A2-E5*A3+E6*A0+E7*A1)+(A4*(-1*G2)-A5*(-1*G3)+A6*G0+A7*(-1*G1))
  D8=(E4*A3+E5*A2-E6*A1+E7*A0)+(A4*(-1*G3)+A5*(-1*G2)-A6*(-1*G1)+A7*G0)
  return(c(D1,D2,D3,D4,D5,D6,D7,D8))
}
